import re

data = '''
홍길동 980311-1231231
아무나 001231-2123121
누구         011111-3445211
'''

print(re.sub(r'(\d{6})-\d{7}', r'\g<1>-*******', data))

import re

data = '''
홍길동 980311-1231231
아무나 001231-2123121
누구         011111-3445211
'''

