re.search(r'([.]?[a-z0-9\-]{3,})([.][a-z0-9\-]{2,})',data)

re.findall(r'[.]?[a-z0-9\-]{3,}([.][a-z0-9\-]{2,})+',data)

re.findall(r'([.]?[a-z0-9\-]{3,})((?:[.][a-z0-9\-]{2,})+)',data)

re.findall(r'(?:([a-z]+):\/\/)?([.]?[a-z0-9\-]{3,})((?:[.][a-z0-9\-]{2,})+)',data)

re.findall(r'(?:([a-z]+):\/\/)?([.]?[a-z0-9\-]{3,})((?:[.][a-z0-9\-]{2,})+)((?:\/[a-z]{3,})*)(#\w+)?',data)

