# https://school.programmers.co.kr/learn/courses/30/lessons/17682 (카카오[1차]다트게임)

def calculate_dart_score(dartResult):
    scores = []
    n = ''

    for i in range(len(dartResult)):
        if dartResult[i].isdigit():
            n += dartResult[i]
        elif dartResult[i] in ['S', 'D', 'T']:
            if dartResult[i] == 'S':
                scores.append(int(n) ** 1)
            elif dartResult[i] == 'D':
                scores.append(int(n) ** 2)
            elif dartResult[i] == 'T':
                scores.append(int(n) ** 3)
        elif dartResult[i] in ['*', '#']:
            if dartResult[i] == '*':
                if len(scores) > 1:
                    scores[-2] *= 2
                scores[-1] *= 2
            elif dartResult[i] == '#':
                scores[-1] *= -1

    return sum(scores)

dart('1S2D*3T'), dart('1D2S#10S'), dart('1D2S0T'), dart('1S*2T*3S'), dart('1D#2S*3S')

bonus = {'S':1, 'D':2, 'T':3}
option = {'*':2, '#':-1}

def dart(s):
    score = list()

    if not re.search(r'(?:\d{1,2}[SDT][*#]?){3}', s):
        return '오류'

    rst = re.search(r'(\d{1,2}[SDT][*#]?)(\d{1,2}[SDT][*#]?)(\d{1,2}[SDT][*#]?)', s)

    if not rst or len(rst.groups()) != 3:
        return '오류'

    for g in rst.groups():
        print(re.search(r'([0-9]{1,2})([SDT])([*#])?', g))
        rst = re.search(r'(\d{1,2})([SDT])([*#])?', g).groups()

        score.append(int(rst[0])**int(bonus[rst[1]])*(option[rst[2]] if rst[2] else 1))

        if rst[2] == '*' and len(score) > 1:
            score[len(score)-2] *= option[rst[2]]
    print(score)
    return sum(score)

