# https://school.programmers.co.kr/learn/courses/30/lessons/72410  (카카오[1차]신규 아이디 추천)

import re

def solution(new_id):
    new_id = new_id.lower()
    new_id = re.sub(r'[^a-z0-9-_.]', '', new_id)
    new_id = re.sub(r'\.+', '.', new_id)
    new_id = new_id.strip('.')

    if not new_id:
        new_id = "a"

    new_id = new_id[:15].rstrip('.')

    while len(new_id) < 3:
        new_id += new_id[-1]

    return new_id

print(solution("...!@BaT#*..y.abcdefghijklm"), solution("z-+.^."), solution("=.="), solution("123_.def"))



